function [v,w] = diffDriveFwdKin(wL,wR,wheelRadius,wheelBase)

    % Initialize DifferentialDrive object
    persistent robot
    if isempty(robot)
        robot = DifferentialDrive(wheelRadius,wheelBase);
    end

    % Run object's forward kinematics method
    [v,w] = forwardKinematics(robot,wL,wR);

end

function [wL,wR] = diffDriveInvKin(v,w,wheelRadius,wheelBase)

    % Initialize DifferentialDrive object
    persistent robot
    if isempty(robot)
        robot = DifferentialDrive(wheelRadius,wheelBase);
    end

    % Run object's forward kinematics method
    [wL,wR] = inverseKinematics(robot,v,w);

end

function bodySpeed = fourWheelSteerFwdKin(wheelSpeeds,wheelRadius,frontWheelDist,rearWheelDist, steerAngles) ...

    % Initialize Four-Wheel Steering object
    persistent robot
    if isempty(robot)
        robot = FourWheelSteering(wheelRadius,[frontWheelDist,rearWheelDist]);
    end

    % Run object's forward kinematics method
    bodySpeed = forwardKinematics(robot,wheelSpeeds,steerAngles);

end

function wheelSpeeds = tripleOmniInverseKinematics(bodySpeed,wheelRadius,wheelAngles, robotRadius)

    % Initialize TripleOmniwheel object
    persistent robot
    if isempty(robot)
        robot = TripleOmniwheel(wheelRadius,robotRadius,wheelAngles);
    end

    % Run object's inverse kinematics method
    wheelSpeeds = inverseKinematics(robot,bodySpeed);

end

