function visualizeRobot(pose, rngs, idx, hasLidar)

    coder.extrinsic('internal.multiRobotVizSet');
    if hasLidar
        internal.multiRobotVizSet(idx,pose,rngs);
    else
        internal.multiRobotVizSet(idx,pose,[]);
    end

end

